import{a as n}from"./q-0a61ba82.js";const c=["Белая Магия","Чёрная Магия","Магия Созидания","Магия Влияния"],s=[{name:"Идеограмма Белой Магии",baseName:"Б И Белой Магии",src:"/menu/magic/runes/Б И Белой Магии.png",branch:"Белая Магия",sideEffects:[`Золотистая аура. Слишком тусклая, чтобы хорошо освещать путь, но слишком яркая, чтобы можно было спрятаться в темном углу.
Свечение тем слабее, чем сильнее чародей магически или физически истощен.
Кроме того, у адептов Белой Магии появляется свечение глаз − тоже светло-золотистое.`,`Золотое сердце. Лёгкие мага начинают слегка просвечивать золотистым светом сквозь кожу и легкую одежду, позволяя, при желании, прочесть письмо при оголённом торсе. Свечение тем слабее, чем сильнее чародей магически или физически истощен.
Сопутствующее светло-золотистое свечение глаз.<br/><i>Конкурс «Побочки» – Сонях</i>`,""],description:"Основная идеограмма Белой Магии",advancedDescription:n("div",null,{class:"advancedDescription"},["Усиливает эффект и длительность только рун своей ветви. В связке со знаками других школ действует иначе:",n("ul",null,null,[n("li",null,null,"Знаки ветви Влияния – облегчает регенерацию энергии мага, позволяя эффективнее плести заклинания.",3,null),n("li",null,null,"Знаки ветви Чёрной магии – ослабляет действие Чёрной магии на цель.",3,null),n("li",null,null,"Знаки ветви магии Мира – предохраняет колдующего от побочных эффектов стихий.",3,null)],3,null),"Основная идеограмма изучается только для того, чтобы создавать сложные заклинания - отдельно не используется.",n("br",null,null,null,3,null)],3,"g3_0")},{name:"Идеограмма Жизни",baseName:"Б И Жизни",src:"/menu/magic/runes/Б И Жизни.png",branch:"Белая Магия",sideEffects:["Волосы (даже на теле) и ногти мага меняют цвет на белый с золотой аурой. Естественный цвет волос временно возвращается в местах концентрации магии.","Волосы мага (даже на теле) и его ногти становятся матово полупрозрачными, подобно затуманенному стеклу.<br/><i>Конкурс «Побочки» – Сонях</i>","Растения и животные, за которыми ухаживает маг, более устойчивы к болезням. Растения под его заботой растут быстрее и обильнее плодоносят."],description:"Воскрешение недавно умершего",advancedDescription:n("div",null,{class:"advancedDescription"},["Воскрешение можно провести только для умершего не более 20 минут назад, приготовившись лечить все угрожающие жизни травмы и пока не началось разложение.",n("br",null,null,null,3,null),"После воскрешения требуется длительный период восстановления: от двух недель до месяца при целом теле, больше для калеки."],3,null)},{name:"Руна Воли",baseName:"Б Р Воли",src:"/menu/magic/runes/Б Р Воли.png",branch:"Белая Магия",sideEffects:["Храброго мага делает еще более стрессоустойчивым, эмоциональному прибавляет благоразумия в его ярости, трусливому даст хитрости при попытке побега.","«Открытая книга». Мысли мага в виде слабо различимых абстракций проецируются золотистым светом на кожу его тела в контролируемых только подсознанием местах.<br/><i>Конкурс «Побочки» – Сонях</i>","Магу требуется значительно меньше внешней мотивации для достижения целей, а его глаза в процессе устремления золотисто светятся. Также это называют наивностью."],description:"Энергия жизни",advancedDescription:n("div",null,{class:"advancedDescription"},"Даёт волю к жизни на последнем издыхании, насыщает цель или мага жизненной силой ценой собственного запаса волевых сил. Не рекомендуется практиковать, самому находясь в депрессии.",3,null)},{name:"Руна Исцеления",baseName:"Б Р Исцеления",src:"/menu/magic/runes/Б Р Исцеления.png",branch:"Белая Магия",sideEffects:["Светящиеся «татуировки», у каждого мага оригинальный узор. Татуировки обладают обеззараживающим свойством.","Одни только прикосновения мага обладают исцеляющим свойством – несколько суток регулярных поглаживаний значительно ускоряют затягивание раны.","Слюна мага обладает слабым обеззараживающим эффектом, а его слезы обладают эффектом простого заживляющего зелья."],description:"Заживление ран",advancedDescription:n("div",null,{class:"advancedDescription"},["Раны легкой, средней и тяжелой степени тяжести, где уровень зависит от навыка.",n("br",null,null,null,3,null),"Яды, болезни и проклятия в эту категорию не входят. Перед использованием рану очищают и изымают из неё инородные объекты."],3,null)},{name:"Руна Выносливости",baseName:"Б Р Выносливости",src:"/menu/magic/runes/Б Р Выносливости.png",branch:"Белая Магия",sideEffects:["Исчезают дефекты внешности, полученные от природы: горбы, кривые зубы, «заячья губа» и т.д.","«Недостижимый идеал». Маг приобретает черты принятого в его культуре канона красоты, несколько утрачивая собственную индивидуальность.<br/><i>Конкурс «Побочки» – Сонях</i>","У мага значительно притупляются ощущения жары, холода, ветра и других стихийных явлений. Повышается риск переохлаждения или теплового удара."],description:"Усиленная стойкость",advancedDescription:n("div",null,{class:"advancedDescription"},"Повышает физическую выносливость цели или мага. В приоритете для воинов в тяжелых доспехах, с двуручным оружием или при марш-бросках на дальние расстояния. После использования цель испытывает усталость в двое сильнее обычной.",3,null)},{name:"Идеограмма Могучести",baseName:"Б И Могучести",src:"/menu/magic/runes/Б И Могучести.png",branch:"Белая Магия",sideEffects:["Приводит чародея в хорошую (не отличную) физическую форму. Облегчает процесс тренировок для достижения отличной физической формы.","Маг становится значительно сильнее физически, но теряет всякую способность контролировать свою силу. Рваные книги, разбитые чашки, дырки на одежде и оторванные дверные ручки становятся его вечными спутниками.<br/><i>Конкурс «Побочки» – Шушундра</i>","Маг уровня новичок или ученик становится тем сильнее физически, чем эмоциональнее он в данный момент. Маг уровня умелец и мастер становится тем физически сильнее, чем он спокойнее."],description:"Физическая мощь",advancedDescription:n("div",null,{class:"advancedDescription"},"Наделяет мага или его цель физической силой, достаточной, чтобы в руках можно было раскрошить камень. Предупреждение: наделяет силой, но не крепостью костей.",3,null)},{name:"Руна Легкости",baseName:"Б Р Легкости",src:"/menu/magic/runes/Б Р Легкости.png",branch:"Белая Магия",sideEffects:["Маг, каким бы аппетитом не обладал, всегда будет стройным.","Сильно притупляет чувство голода и немного уменьшает потребность в пище. Существует риск истощения.<br/><i>Конкурс «Побочки» – Сонях</i>",""],description:"Воздушность и левитация",advancedDescription:n("div",null,{class:"advancedDescription"},"Не уменьшает массу, но уменьшает вес, поднимая в воздух с помощью магической силы (максимум 100 кг для одиночной руны). Контролировать парение невозможно — ближайшие предметы, плоскости и противодействующие силы в помощь.",3,null)},{name:"Руна Гибкости",baseName:"Б Р Гибкости",src:"/menu/magic/runes/Б Р Гибкости.png",branch:"Белая Магия",sideEffects:["Упругость костей. При сильном эффекте маг скорее получит очень болезненный ушиб или глубокую рану, чем сломает кость.","Упругость тканей. Кожа, жир и мышцы мага становятся настолько эластичными, что могут сжиматься вплоть до формы кости. Так, например, толстый маг сможет просочиться в любую дверь не уже его скелета.<br/><i>Конкурс «Побочки» – Сонях</i>","Упругость крови. Артерии и вены мага разбухают как при варикозе. При ранении кровь мага необычайно густа и упруга, что не допускает кровотечения."],description:"Неестественная эластичность",advancedDescription:n("div",null,{class:"advancedDescription"},"Наделяет цель или мага повышенной гибкостью, из-за чего раньше несгибающиеся суставы спокойно дадут сделать шпагат даже на двух катящихся рядом телегах.",3,null)},{name:"Руна Покоя",baseName:"Б Р Покоя",src:"/menu/magic/runes/Б Р Покоя.png",branch:"Белая Магия",sideEffects:["Придает черту характера необычайной сдержанности и кротости.","Значительно снижает эмпатию мага и его потребность в социализации.<br/><i>Конкурс «Побочки» – Сонях</i>","Если маг спокоен, то при общении с эмоциональными персонажами ими тоже постепенно овладевает спокойствие."],description:"Категорическая безмятежность",advancedDescription:n("div",null,{class:"advancedDescription"},"Наделяет цель умиротворением и притуплением чувств. Применяется в полевой медицине, облегчая работу медику.",3,null)},{name:"Идеограмма Черной Магии",baseName:"Ч И Черной Магии",src:"/menu/magic/runes/Ч И Черной Магии.png",branch:"Чёрная Магия",sideEffects:["Красно-оранжевая аура. Она особенно ярка, когда чародей находится в местах, наполненных страданиями − например, посреди только что сожженной дотла деревни. Кроме того, у адептов Чёрной Магии появляется свечение глаз, похожее на жидкое пламя.","По локоть в крови. Кожа на руках мага, начиная от кончиков пальцев, становится липкой и густо-мокрой на ощупь. В запущенных ситуациях покрываются багровым цветом.<br/><i>Конкурс «Побочки» – Сонях</i>","Чем больше на маге надето предметов, бывших в местах, наполненных страданиями, тем сильнее на него влияют эффекты рун Несчастья и Хаоса, но тем слабее на него влияют эффекты остальных рун Черной магии. При поглощении эффектов мага покрывает яркая аура Черной магии."],description:"Основная идеограмма Чёрной Магии",advancedDescription:n("div",null,{class:"advancedDescription"},["Усиливает эффект и длительность только рун своей ветви. В связке со знаками других школ действует иначе:",n("ul",null,null,[n("li",null,null,"Знаки ветви Белой магии – ослабляет эффективность воздействия на цель.",3,null),n("li",null,null,"Знаки ветви Влияния – придает рунам хаотичную нестабильность.",3,null),n("li",null,null,"Знаки ветви Созидания – вероятность критических эффектов стихий слегка увеличивается.",3,null)],3,null),"Основная идеограмма изучается только для того, чтобы создавать сложные заклинания - отдельно не используется.",n("br",null,null,null,3,null)],3,null)},{name:"Идеограмма Смерти",baseName:"Ч И Смерти",src:"/menu/magic/runes/Ч И Смерти.png",branch:"Чёрная Магия",sideEffects:["Придаёт коже аристократичную бледность и старческую сухость.","Лик смерти. На лице мага, вокруг глаз, рта, под скулами, на висках и носу залегают чёрные пигментные пятна, придающие ему вид черепа.<br/><i>Конкурс «Побочки» – Сонях</i>","Смертельные болезни мага или его окружающих усиливаются в симптомах, но никогда не приблизятся к терминальной стадии сами по себе."],description:"Смерть классическая",advancedDescription:n("div",null,{class:"advancedDescription"},"Просто и банально, зато уважаемо уже много веков после Красного Рассвета – убить цель на месте. Руна сильно зависит от цели и профессионализма мага: молодой и здоровый человек станет плохо себя чувствовать. Старик отдаст богам душу сразу же.",3,null)},{name:"Руна Разрушения",baseName:"Ч Р Разрушения",src:"/menu/magic/runes/Ч Р Разрушения.png",branch:"Чёрная Магия",sideEffects:["Ногти на руках и ногах превращаются в черные когти. На поздних стадиях внешне чернеют зубы вплоть до угольно-черного.","Всесокрушение. (1/2) Одно случайное из десяти прикосновений мага окажет на объект слабый эффект руны. Придётся носить перчатки, но и на них эффект довольно быстро сказывается.<br/><i>Конкурс «Побочки» – Сонях</i>","Иммунитет мага сильно ослабевает для мелкой заразы – насморк, акне и герпес теперь вечные спутники мага; но заметно усиливается для всего остального."],description:"Деструктивные процессы",advancedDescription:n("div",null,{class:"advancedDescription"},"Ржавчина, гниль, плесень, грибок, язвы, кариес – всё самое вкусное и другие медлительные деструктивные процессы. При регулярной практике ускоряется достижение окончательного эффекта.",3,null)},{name:"Руна Несчастья",baseName:"Ч Р Несчастья",src:"/menu/magic/runes/Ч Р Несчастья.png",branch:"Чёрная Магия",sideEffects:[`Изменяет форму зубов чародея так, что они становятся по-хищному острыми.
В азартных играх ему и остальным игрокам одинаково не везёт.`,"Мага преследует зловещая тень, сопровождающая все неудачи, происходящие с ним и теми, кто его окружает. Количество неудач мага и окружающих значительно увеличивается, но он (и только он) может видеть как тень «тянется» к объекту, что даёт магу крохотный шанс эту неудачу предотвратить или как минимум к ней приготовиться. Тень теряет власть при применении поблизости белой магии.<br/><i>Конкурс «Побочки» – Maria</i>","Маг становится карикатурно суеверным, его голос становится тихим и дрожащим. Постоянно ожидает беды, при том что статистически испытывает меньше неудач чем обычный человек."],description:"Критическая неудача",advancedDescription:n("div",null,{class:"advancedDescription"},"Её опасаются новички, но нежно любят мастера Чёрной магии. Стоит вплести её в заклинание, как враг получит критический эффект. В одиночном варианте создает удручающую атмосферу вокруг цели: тускнеют краски, слышатся завывания, мелькают тени, учащаются мелкие неудачи. Убегая от маньяка, жертва обязательно споткнется.",3,null)},{name:"Руна Хаоса",baseName:"Ч Р Хаоса",src:"/menu/magic/runes/Ч Р Хаоса.png",branch:"Чёрная Магия",sideEffects:["На поздних стадиях при переживании определенной эмоции чародей за несколько секунд плавно меняет цвет волос и кожи на тот, с которым у него ассоциируется эта эмоция. Цвет уходит, когда маг успокоится.","Всесокрушение. (2/2) Одно случайное из пяти действий группы, в которой состоит маг (или его собственное в одиночестве) становится неудачным, даже если ничто не предвещало, либо наоборот приобретает некоторый положительный окрас, даже если действие по своей сути нейтрально.<br/><i>Конкурс «Побочки» – Сонях</i>","Голос мага становится резким, спонтанно срывающимся на крик. Таким же или иным образом страдает дикция и у всех, кто его окружает."],description:"Непорочная случайность",advancedDescription:n("div",null,{class:"advancedDescription"},"Старший брат Руны Несчастья. Руна, которую все маги люто ненавидят, особенно если её использует противник. Она навязывает цели эффект первозданной случайности: будь то хаотичная траектория заклинания, случайный выбор цели или применение эффектов в непроизвольном порядке (включая самого колдующего). Если использовать на себя - мало не покажется.",3,null)},{name:"Идеограмма Слабости",baseName:"Ч И Слабости",src:"/menu/magic/runes/Ч И Слабости.png",branch:"Чёрная Магия",sideEffects:["Маг становится дряхлым и худощавым.","Маг нуждается в большем количестве сна. Постоянная легкая светобоязнь.","У мага значительно повышается чувствительность к боли."],description:"Обострение уязвимостей",advancedDescription:n("div",null,{class:"advancedDescription"},"Цель становится более уязвима к любого вида урону, к болезням, жертве становится тяжело двигать собственными конечностями, ментальная слабость проявляется в неконтроллируемых эмоциях.",3,null)},{name:"Руна Тяжести",baseName:"Ч Р Тяжести",src:"/menu/magic/runes/Ч Р Тяжести.png",branch:"Чёрная Магия",sideEffects:["Классическая для злодеев сутулость. В тяжелых случаях горбатость.","Маг теряет ощущение собственного веса, из-за чего он ходит то в припрыжку, то ели волочит ноги.<br/><i>Конкурс «Побочки» – Сонях</i>","У мага сильно уменьшается скорость реакции."],description:"Увеличение массивности",advancedDescription:n("div",null,{class:"advancedDescription"},"Обратное действие лёгкости: цель так сильно тянет к земле, словно к ногам привязаны гири, на шее висит камень, а в руках – полный бушель. В истории зафиксированы неоднократные случаи ломки костей при применении усиленного варианта.",3,null)},{name:"Руна Жесткости",baseName:"Ч Р Жесткости",src:"/menu/magic/runes/Ч Р Жесткости.png",branch:"Чёрная Магия",sideEffects:["Голос темного мага становится низким и приобретает злодейские нотки: хрипотцу, утробность и т.д. У разных магов с разным оттенком.","Маг становится медленнее и в целом менее ловким.","Огрубение мышц лица и слезных желез ограничивает способность мага выражать эмоции и делает невозможным плачь."],description:"Потеря подвижности",advancedDescription:n("div",null,{class:"advancedDescription"},"Цель или маг теряет всякую эластичность, подвижность отдельных составных частей, жидкости начинают вести себя как твёрдые тела и чем больше опыта — тем жесче. Человек под воздействием руны становится парализованным и в то же время более устойчивым к внешним (да и внутренним) повреждениям.",3,null)},{name:"Руна Ярости",baseName:"Ч Р Ярости",src:"/menu/magic/runes/Ч Р Ярости.png",branch:"Чёрная Магия",sideEffects:["Голову мага венчают рога разнообразной формы, но всегда в естественный цвет волос. Если другое влияние изменило цвет волос, рога остаются того цвета, какого у мага были волосы с рождения.","Когда незнакомцы целенаправленно проявляют к магу агрессию, у него в один из трёх случаев возникнет приступ непреодолимого страха и паники. Работает и в обратную сторону: в один из трёх случаев незнакомец испытает страх и панику при агрессии со стороны мага.<br/><i>Конкурс «Побочки» – Сонях</i>","Маг начинает испытывать эффект «стокгольмского синдрома» к любым своим оппонентам от сочувствия и уважения вплоть до любви и страсти. Его убедительность при выражении этих чувств также повышается.<br/><i>Конкурс «Побочки» – Maria</i>"],description:"Жажда крови",advancedDescription:n("div",null,{class:"advancedDescription"},"Наделяет цель бесконтрольной жаждой крови. Адепты Чёрной магии предпочитают практиковаться в переполненных тавернах, однако на поле боя, чтобы посеять панику в рядах противника, эта руна тоже пригодится.",3,null)},{name:"Идеограмма Созидания",baseName:"С И Созидания",src:"/menu/magic/runes/С И Созидания.png",branch:"Магия Созидания",sideEffects:["Фиолетовая аура. Она постоянна и отличается от других трех тем, что оставляет после себя визуальный шлейф, а порой даже остается светящимися пятнами везде, где прикасался или находился маг (на короткое время). Кроме того, у адептов Магии Созидания появляется свечение глаз – насыщенно-сиреневое.","Повышает предрасположенность к музыкальному, изобразительному и практически любому другому искусству того мага, который уже имел к этому искусству талант. Чем лучше маг осваивает искусство, тем ярче фиолетовая аура вокруг него в процессе творчества.",""],description:"Является основой для любой другой руны данной ветви, усиливает и продлевает эффект только нижеследующих рун.",advancedDescription:n("div",null,{class:"advancedDescription"},["Усиливает эффект и длительность только рун своей ветви. В связке со знаками других школ действует иначе:",n("ul",null,null,[n("li",null,null,"Знаки ветви Белой магии – добавляет к действию согревающий или охлаждающий эффект.",3,null),n("li",null,null,"Знаки ветви магии Влияния – придает заклинаниям красивую визуализацию стихий.",3,null),n("li",null,null,"Знаки ветви Чёрной магии – добавляет всем рунам легкий стихийный урон.",3,null)],3,null),"Основная идеограмма изучается только для того, чтобы создавать сложные заклинания - отдельно не используется.",n("br",null,null,null,3,null)],3,null)},{name:"Идеограмма Звука",baseName:"С И Звука",src:"/menu/magic/runes/С И Звука.png",branch:"Магия Созидания",sideEffects:["Чародей начинает говорить гармониями, его тембр представляет из себя созвучие нескольких нот. Положительно настроенный персонаж говорит консонансами, злодей – диссонансами.","С годами маг приобретает глухоту, но тем лучше осваивает способность воспринимать звук через вибрации костей. Так например, полностью оглохший маг может услышать, казалось бы, бесшумные шаги.<br/><i>Конкурс «Побочки» – Шушундра</i>","Абсолютный слух. Маг, даже не обладавший какими-либо музыкальными умениями, обретает абсолютный и очень чуткий слух. Маг способен слышать и различать звуки на больших расстояниях, определять ноты, но диссонансы причиняют ему боль и физический вред."],description:"Акустическое воздействие",advancedDescription:n("div",null,{class:"advancedDescription"},"Создает полную тишину или же сильно повышает громкость существующего звука с возможностью влиять на тон. Действие магии распространяется в твёрдой, жидкой или газообразной среде. Используется как замена рупору или в случаях, когда утомляет шум с улицы или необходимо наложить на определенный участок защиту от лишних ушей. Некоторые ушлые чародеи используют для приглушения шагов.",3,null)},{name:"Руна Воздуха",baseName:"С Р Воздуха",src:"/menu/magic/runes/С Р Воздуха.png",branch:"Магия Созидания",sideEffects:["Движения мага становятся резкими и порывистыми.","Чихи мага становятся настолько мощными, что могут раскидать по комнате большую стопку бумаг.<br/><i>Конкурс «Побочки» – Шушундра</i>",""],description:"Воздействие ветром",advancedDescription:n("div",null,{class:"advancedDescription"},"Позволяет создавать потоки воздуха (управлять созданным нельзя, только создать вектор движения). Маги обычно используют эту руну, если столкновение в землей всем телом неизбежно — соломку не постелешь, зато о воздух затормозишь. Эта же руна, используемая должным образом, является руной охлаждения, в противовес руне огня.",3,null)},{name:"Руна Молнии",baseName:"С Р Молнии",src:"/menu/magic/runes/С Р Молнии.png",branch:"Магия Созидания",sideEffects:["Волосы (даже на теле) всегда стоят дыбом, статически заряжены. Постоянный риск возгорания окружающих мага жидкостей и газов.","Из-за статического заряда к рукам и ногам мага всегда липнут фантики, бумага, пыль и шерсть. Носимая шерстяная одежда будет «кусать» постоянно как его самого, так и окружающих. Постоянный риск возгорания окружающих мага воспламеняющихся жидкостей и газов.<br/><i>Конкурс «Побочки» – Шушундра</i>","Маг начинает чувствовать электрические поля: он способен задолго предсказывать дожди, но сам притягивает молнии и полностью статически заряжен во время грозы."],description:"Беспощадное электричество",advancedDescription:n("div",null,{class:"advancedDescription"},"Руна, которую не используют без заземления. Применяется в основном против «консервных банок» с мечами и щитами или против барахтающихся в луже пьянчуг.",3,null)},{name:"Идеограмма Пелены",baseName:"С И Пелены",src:"/menu/magic/runes/С И Пелены.png",branch:"Магия Созидания",sideEffects:["Вокруг мага буквально сгущаются тени, иногда обвисая на нём как прозрачный антрацитовый туман.","Тело мага неоднородно (пятнами) едва заметно полупрозрачно, будто состоит из тумана.<br/><i>Конкурс «Побочки» – Maria</i>","Маг приобретает талант ориентироваться даже в полной темноте и при сильном тумане, но расплачивается за это светобоязнью."],description:"Мистические туманы",advancedDescription:n("div",null,{class:"advancedDescription"},"Создаёт область из клубящегося абсолютно чёрного дыма, либо классического безвредного парового тумана: в нем ничего не видно, также сильно заглушены запахи и звуки. В дыму тяжело дышать, но какие-либо вредные газы, помимо обычного воздуха, отсутствуют.",3,null)},{name:"Руна Огня",baseName:"С Р Огня",src:"/menu/magic/runes/С Р Огня.png",branch:"Магия Созидания",sideEffects:["Придает коже темный оттенок, равносильный сильному загару. Нормальная температура его тела до 10 градусов выше.","Маг становится менее чувствителен к ожогам, но тяжелее переживает холода.",""],description:"Излюбленное пламя",advancedDescription:n("div",null,{class:"advancedDescription"},"Самая популярная руна, которую испробовали в десятках вариаций, начиная от искр для прикуривания папироски и заканчивая огненными шарами.",3,null)},{name:"Руна Воды",baseName:"С Р Воды",src:"/menu/magic/runes/С Р Воды.png",branch:"Магия Созидания",sideEffects:["Характер смягчается до того, что маг будет всеми силами избегать конфликта.","Маг всплывает в воде без лишних телодвижений, что не усложняет его нахождение под водой, но наоборот делает его более быстрым и подвижным.",""],description:"Власть над жидкостями",advancedDescription:n("div",null,{class:"advancedDescription"},"Дает власть над водой, туманом, а также позволяет конденсировать жидкость в любой емкости (включая стаканы и заканчивая глоткой недруга).",3,null)},{name:"Идеограмма Силы",baseName:"С И Силы",src:"/menu/magic/runes/С И Силы.png",branch:"Магия Созидания",sideEffects:["Руки чародея украшают рунические браслеты в стиле Магии Созидания, узор всегда оригинальный.","Повышенный тон мага оказывает реальную физическую силу, способную столкнуть неловкого человека с ног.","Чародей способен бросать и толкать предметы дальше, но при попытке поднять предмет он может спонтанно двинуться в случайном направлении."],description:"Направленный силовой удар",advancedDescription:n("div",null,{class:"advancedDescription"},"Позволяет магу применить к цели импульс в одном конкретном направлении, не зависящем от его собственного расположения. Средний, умеренно опытный маг сможет опрокинуть плотный, набитый шкаф или отправить в голову вазу с пробитием черепа. В сложном заклинании, применив два или более действий в разные стороны, может разорвать цель на ошметки, щепки, осколки.",3,null)},{name:"Руна Земли",baseName:"С Р Земли",src:"/menu/magic/runes/С Р Земли.png",branch:"Магия Созидания",sideEffects:["Ранняя седина, но вопреки ожиданиям волосы становятся крепки, как гвоздями прибитые, и почти никогда не выпадают.","Маг способен ходить босиком без какого-либо дискомфорта.",""],description:"Манипуляция породами",advancedDescription:n("div",null,{class:"advancedDescription"},"Управление горными породами, песком и глиной. Варианты использования заканчиваются в границах их естественного природного вида. С суррогатами и производными (стекло, слитки металла, ювелирные украшения) руна Земли не работает. Одиночная руна позволяет управлять и менять форму материала весом не более 300 кг, а его рыхление или уплотнение возможно в радиусе 10 м.",3,null)},{name:"Руна Льда",baseName:"С Р Льда",src:"/menu/magic/runes/С Р Льда.png",branch:"Магия Созидания",sideEffects:["Чародей теряет чувственность, становится менее эмоциональным и менее сексуально заинтересованным.","Прикосновения мага оказывают охлаждающее воздействие, и за полчаса могут охладить тёплый предмет вплоть до 4°С.<br/><i>Конкурс «Побочки» – Шушундра</i>","Маг становится менее чувствителен к холоду, но тяжелее переживает жар."],description:"Замораживающая сила",advancedDescription:n("div",null,{class:"advancedDescription"},"Острые ледяные шипы, полная заморозка цели, пара кубиков льда в крепкий коньяк.",3,null)},{name:"Идеограмма Влияния",baseName:"В И Влияния",src:"/menu/magic/runes/В И Влияния.png",branch:"Магия Влияния",sideEffects:["Голубовато-зеленая аура. Она становится ярче при использовании магии или нахождении поблизости от Мест Силы. Характерно проявление эфемерных лент, окутывающих мага наподобие шали. Кроме того, у адептов Магии Влияния появляется свечение глаз – бирюзовое или зелёное.","Мудрость. Глаза мага становятся ярко зелеными, его взгляд приобретает дивную проницательность, позволяющую «читать» людей, видеть их намерения и чувства, если они смотрят ему в глаза. Свечение постоянно и даже после смерти не пропадает на протяжении нескольких лет.",""],description:"Является основой для любой другой руны данной ветви, усиливает и продлевает эффект только нижеследующих рун.",advancedDescription:n("div",null,{class:"advancedDescription"},["Усиливает эффект и длительность только рун своей ветви. В связке со знаками других школ действует иначе:",n("ul",null,null,[n("li",null,null,"Знаки ветви Белой магии – слегка повышает точность применения.",3,null),n("li",null,null,"Знаки ветви Чёрной магии – повышает стабильность заклинаний на 10%.",3,null),n("li",null,null,"Знаки ветви магии Созидания – вероятность промаха уменьшается на 10%.",3,null)],3,null),"Основная идеограмма изучается только для того, чтобы создавать сложные заклинания - отдельно не используется.",n("br",null,null,null,3,null)],3,null)},{name:"Идеограмма Формы",baseName:"В И Формы",src:"/menu/magic/runes/В И Внимания.png",branch:"Магия Влияния",sideEffects:["Черты лица колдуна становятся резкими и острыми, фанатичный колдун станет походить на угловатую каменную статую.","Все заклинания колдуна приобретают упрощенную геометричную форму, а потому их становится сложнее распознать. Так, например, огненный шар примет форму идеальной красной сферы, вокруг которой теплом искажается свет.",""],description:"Временная магическая конструкция",advancedDescription:n("div",null,{class:"advancedDescription"},"Создаёт осязаемую магическую форму любого агрегатного состояния простой геометрии. Твёрдость формы ровно такая, сколько энергии в нее влито: заклинание простой руны рвётся как картон. Также густота жидкости определяется количеством влитой энергии. По истечении времени заклинания, магия рассеивается в среду.",3,null)},{name:"Руна Концентрации",baseName:"В Р Концентрации",src:"/menu/magic/runes/В Р Духа.png",branch:"Магия Влияния",sideEffects:["Развивает особую внимательность. Внимательность не выборочная, поэтому очень часто маг впадает в состояние гиперфиксации на какой-то ерунде.","При попытке что-то вспомнить в сознании мага возникает полупрозрачная энциклопедия в цветах ветви, в которой содержатся все знания, которые он так же пытался вспомнить до этого. Случайное воспоминание исчезает из книги, если маг так и не вспомнил то, что хотел.<br/><i>Конкурс «Побочки» – Maria</i>","Синдром Понтия Пилата. Испытывая раздражение, страх или стыд маг начинает судорожно приводить в порядок своё, снаряжение, оборудование, жильё, как бы желая обрести контроль над ситуацией."],description:"Улучшение когнитивных функций",advancedDescription:n("div",null,{class:"advancedDescription"},"Усиливает концентрацию мага или цели, увеличивает скорость реакции, чуткость восприятия, чистоту мысли, скорость освоения информации, обучения.",3,null)},{name:"Руна Истока",baseName:"В Р Истока",src:"/menu/magic/runes/В Р Истока.png",branch:"Магия Влияния",sideEffects:["Цвет волос меняется на белоснежный. Естественный цвет волос временно возвращается в местах концентрации магии.","Волосы тяжелеют, начинают себя вести, будто бы всё время погружены в воду. В местах сильных магических возмущений тянутся к эпицентру.<br/><i>Конкурс «Побочки» – Шушундра</i>","Магия интроверта. В полном уединении (ну души в радиусе 100 метров) маг восстанавливает магию в два раза быстрее."],description:"Присутствие магии",advancedDescription:n("div",null,{class:"advancedDescription"},"Позволяет найти ближайшее Место Силы или любое другое сильное магическое явление. Поиск показывает направление, опытный маг чувствует примерное растояние. Позволяет поделиться своей энергией с другим магом.",3,null)},{name:"Руна Притока",baseName:"В Р Притока",src:"/menu/magic/runes/В Р Притока.png",branch:"Магия Влияния",sideEffects:["Cнижает способности к выражению эмпатии до минимума, но способность понимать и чувствовать эмоции других обостряется.","Мнение толпы. Чем больше людей окружают мага, тем меньше его эмоции зависят от него и больше подражают общему настроению толпы.","Картина мира. Маг начинает воспринимать мир как совокупность взаимосвязанных систем, а не как набор отдельных явлений. Это может привести к открытиям и прозрениям, но всегда приводит к высокомерию и презрению в отношении «непросвещенных»."],description:"Отсутствие магии",advancedDescription:n("div",null,{class:"advancedDescription"},"Страшненькая подруга Руны Истока. Позволяет скрыть присутствие магии, создав барьер или стену. Также позволяет забирать энергию другого мага, если тот не сопротивляется — находится в полностью бессознательном состоянии; во время сна сопротивление гораздо слабее, но возможно.",3,null)},{name:"Идеограмма Единства",baseName:"В И Единства",src:"/menu/magic/runes/В И Единства.png",branch:"Магия Влияния",sideEffects:["Непроизвольные всплески эмоций, от беспричинной радости до впадения в глубокую депрессию на непродолжительное время.","Случайные неразборчивые отрывки мыслей и эмоций мага забредают в головы находящихся вокруг него. Все способные к речи инстинктивно избегают чародея.","Мозговой штурм. Каждую вторую мысль маг воспринимает, как сказанную извне, от чего он начинает разговаривать и вести полноценный диалог сам с собой."],description:"Связывание и распределение",advancedDescription:n("div",null,{class:"advancedDescription"},["Соединяет цели заклинанием, вследствие чего негативные и позитивные эффекты разделяются между ними. Например, боль от ножевой раны, действие руны Исцеления, или ослабляющий эффект (порча, проклятие).",n("br",null,null,null,3,null),"В составе сложного заклинания соединить можно до десяти целей (следите, чтобы в цепочку не попал противник)."],3,null)},{name:"Руна Пути",baseName:"В Р Пути",src:"/menu/magic/runes/В Р Пути.png",branch:"Магия Влияния",sideEffects:["Не зря про таких говорят «потерялся», ведь маг зарабатывает топографический кретинизм.","Маг напрочь забывает место из которого перемещался предпоследний раз.<br/><i>Конкурс «Побочки» – Cento</i>","Маг становится настолько рассеянным, что поставив на месте предмет, в двух из трёх случаев он будет находить его в другом месте поблизости.<br/><i>Конкурс «Побочки» – Шушундра</i>"],description:"Перемещение материи",advancedDescription:n("div",null,{class:"advancedDescription"},["Руна перемещения, телепортации. Базовая дальность — 10км в любом направлении. При впечатывании на молекулярном уровне в скалы, деревья или лесное зверье виноват только плохо подготовившийся маг, поэтому рекомендуется использовать с основной руной Влияния и Концентрации, чтобы путешествие было успешным.",n("ul",null,null,[n("li",null,null,"Простая руна — одна цель;",3,null),n("li",null,null,"В сложном заклинании — полноценный двухсторонний портал, непрозрачный и звуконепроницаемый.",3,null)],3,null)],3,null)},{name:"Руна Фокуса",baseName:"В Р Фокуса",src:"/menu/magic/runes/В Р Фокуса.png",branch:"Магия Влияния",sideEffects:["При длительном использовании оказывает на адепта эффект «Пламенного Взора». Глаза мага на дистанцию до 25 сантиметров приобретают свечение в виде зеленоватых лучей, которые отображают направление взгляда. Бонусом немного улучшают зрение обладателя в любое время дня и ночи","Гиперфиксация. Обратив внимание на какой-то объект, маг перестает воспринимать движение других объектов, но не потеряет из виду объект фиксации даже на очень значительных расстояниях.<br/><i>Конкурс «Побочки» – Шушундра</i>",`В тёмных помещениях маг фокусирует на себе и без того редкие крупицы света. Отраженный свет недостаточен чтобы освещать путь.
В ярких помещениях свет попадающий прямо на мага рассеивается, что делает его заметно темнее окружения. Отраженный свет в виде солнечного зайчика летит в случайном направлении.`],description:"Линза магической энергии",advancedDescription:n("div",null,{class:"advancedDescription"},"Сводит действие обширного заклинания в одну точку, либо, наоборот, рассеивает заклинание по области, уменьшая мощность. Для концентрации струи пламени в луч одной руны будет мало.",3,null)},{name:"Руна Вмешательства",baseName:"В Р Вмешательства",src:"/menu/magic/runes/В Р Вмешательства.png",branch:"Магия Влияния",sideEffects:["Эффект бабочки. Синдром поиска глубинного смысла, казалось бы, даже в самых незначительных вещах, излишняя осторожность и паранойя. Возникающие у мага острые предчувствия часто сбываются, но еще чаще ложны.","Маг обретает привычку или даже потребность принимать участие в других разговорах. Начинается всё с неспособности сопротивляться подслушиванию, но в конечном итоге маг влезет в разговор, даже абсолютно не имея знаний в теме дискуссии.<br/><i>Конкурс «Побочки» – Шушундра</i>","Если бы да кабы. Попав в ситуацию, разум мага заполоняют альтернативные варианты развития событий, которые бы произошли, не сделай он что-либо в прошлом. Альтернативы эти до ужаса точны и подробны, настолько, что из них можно извлечь опыт."],description:"Естественная реконфигурация",advancedDescription:n("div",null,{class:"advancedDescription"},"Позволяет вторгнуться в уже созданное заклинание и изменить его соответственно рунам в связке с которыми была применена, в виде одиночной руны позволяет изменить направление или любой другой один простой параметр. Без опыта работы с конкретной комбинацией результаты крайне непредсказуемы.",3,null)},{name:"Неизвестная идеограмма",baseName:"В И Unknown",src:"/menu/magic/runes/blank.png",branch:"Магия Влияния",sideEffects:["","",""]},{name:"Руна Вести",baseName:"В Р Вести",src:"/menu/magic/runes/В Р Вести.png",branch:"Магия Влияния",sideEffects:["Очень сильно нарушает конспирацию чародеев, злоупотребляющих ею: вокруг головы начинает вертеться «обруч» из прозрачных слов, которые чаще всего передавались именно этим магом через руну Вести. Сначала зеленоватое свечение почти незаметно, но в запущенном случае будет выглядеть как сияющий зеленый нимб.","«Место для вашей рекламы». Кожу мага в контролируемых только его подсознанием местах покрывают зелёные татуировки с текстом всех реплик, произносимых вокруг него, даже если сам он слушает кого-то конкретного или не слушает никого вовсе.<br/><i>Конкурс «Побочки» – Шушундра</i>","Шепот. Звук ветра превращается для мага в неразборчивый шёпот. Шум очень отвлекает, но время от времени или приложив значительные усилия чародей может выудить из шума отдельные слова, относящиеся к новостям в регионе или мире."],description:"Передача магических сообщений",advancedDescription:n("div",null,{class:"advancedDescription"},["В полом участке простой руны пишется сообщение и отправляется чётко представленному адресату, иначе возможна потеря или утечка информации. Чтобы вместить больше текста, нужно создавать сложное заклинание.",n("ul",null,null,[n("li",null,null,"Одиночная руна − не более 10 км. Сложное заклинание без усиливающей руны − максимум 50 км (если все руны − руны Вести). Сложное с усилением − максимум 100 км.",3,null),n("li",null,null,"Сообщение затеряется, если между отправителем и адресатом находится место силы или любое другое сильное магическое явление.",3,null)],3,null)],3,null)},{name:"Руна Роста",baseName:"В Р Роста",src:"/menu/magic/runes/В Р Роста.png",branch:"Магия Влияния",sideEffects:["Крепкие ногти и красивые здоровые волосы у мага сопутствуют с раздраженной кожей: чуть что − прыщи, чешется, шелушится и т.д.","Неконтролируемый рост волос, который может привести к образованию колтунов или даже дредов. Стрижка потребуется не реже чем раз в неделю.",""],description:"Трансформация живой материи",advancedDescription:n("div",null,{class:"advancedDescription"},"Позволяет вырастить волосы, ногти, зубы, кости или плоть. Действует только на живую ткань. Скорость простой руны - два сантиметра квадратных ткани в секунду (волос и ногтей тоже касается). При применении необходимо убедиться, что у организма достаточно сил, чтобы выдержать подобную нагрузку регенерации. Можно срастить культю и конечность, если последняя будет в наличии. С нуля конечность (тем более голову) создать не получится - это будет кусок плоти с нежной розовой кожицей.",3,null)},{name:"Неизвестная руна",baseName:"В Р Unknown",src:"/menu/magic/runes/blank.png",branch:"Магия Влияния"}];function r(){let e=[];return s.forEach(a=>{const i={...a};delete i.advancedDescription,e.push(i)}),e}export{c as b,r as g,s as r};
